score = int(input("Enter score between 1 and 100: "))
if score < 40:
    grade = 'F'
elif score < 50:
    grade = 'E'
elif score < 60:
    grade = 'D'
elif score < 70:
    grade = 'C'
elif score < 80:
    grade = 'B'
else:
    grade = 'A'

print("Your grade is %s" %(grade))

visited = []
queue = []
def bfs(visited, graph, node):
  visited.append(node)
  queue.append(node)
  #print('visited=',node)
  while queue:
    s = queue.pop(0)
    print(s, end='')

    for neighbour in graph[s]:
      if neighbour not in visited:
        visited.append(neighbour)
        #print('visited=',neighbour)
        queue.append(neighbour)
        #print('queue=',neighbour)

    if len(queue) > 0: print(' > ', end='')

n = var()

@match(0)
